import './index.css'
import { useEffect } from 'react';

const Modal = ({ post, onCloseModal}) => {

  const handleModalClick = (e) => {
    e.stopPropagation();
  };

    return ( 
      <div className="modal" onClick={onCloseModal}>
      <div className="modal__content" onClick={handleModalClick}>
        <div>{post.title}</div>
        <div>{post.text}</div>
        <div className="close" onClick={onCloseModal}>close</div>
      </div>
    </div>
    );
  }


  export default Modal;
