import './style.css';
import React from 'react';
import logo from './../../images/logo.png';
import search from './../../images/search.png';
import arrow_bottom from './../../images/arrow_bottom.svg';
import { navMenu } from '../../configs/navMenu.js';
import { useState } from 'react';
import { useEffect } from 'react';
import Menu from '../Menu/Menu';
import axios from 'axios';

const Header = () => {
  const [menuActive, setMenuActive] = useState(false);
  const [search, setSearch] = useState(''); 






  const [searchTerm, setSearchTerm] = useState("");
  const [carList, setCarList] = useState([]);

  useEffect(() => {
    axios
      .get("https://example.com/cars")
      .then((response) => {
        setCarList(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const filterCars = (searchText, listOfCars) => {
    if (!searchText) {
      return listOfCars;
    }
    return listOfCars.filter(({ car_model }) =>
      car_model.toLowerCase().includes(searchText.toLowerCase())
    );
  };

  const filteredCars = filterCars(searchTerm, carList);







  return ( 
    <header className='header'>
        <div className="container">
          <div className='header__top'>
            <img src={logo} alt="logo" className="logo" />
            <div className="search">
              <img src={search} alt="search_icon" className="search__img" />
            </div>
          </div>

          <nav className="nav">

              <div className={menuActive ? 'burger-btn active' : 'burger-btn'} onClick={() => setMenuActive(!menuActive)}>
                <span/>
              </div>

            <ul className="nav-list">
              {navMenu.map(item => (
                <li className="nav-list__item">
                  <a href="#" className="nav-list__link">{item}</a>
                  <img src={arrow_bottom} alt="arrow" className="nav-list__arrow" />
                </li>
              )
              )}
            </ul>
          </nav>
        </div>
        <Menu active={menuActive} setActive={setMenuActive}/>
        <input
        value={searchTern}
        autoFocus
        type='text'
        placeholder='Поиск...' 
        onChange={e => setSearchTern(e.target.value)}
        
        />
      </header>
  );
}

export default Header;
