import './style.css';
import logo from './../../images/logo.png';
import search from './../../images/search.png';
import arrow_bottom from './../../images/arrow_bottom.svg';
import { navMenu } from '../../configs/navMenu.js';
import { useState } from 'react';

const Header = () => {
  const [menuActive, setMenuActive] = useState
  return ( 
    <header className='header'>
        <div className="container">
          <div className='header__top'>
            <img src={logo} alt="logo" className="logo" />
            <div className="search">
              <img src={search} alt="search_icon" className="search__img" />
            </div>
          </div>

          <nav className="nav">
            <div className="burger-menu">
              <div className='burger-btn'>
                <span/>
              </div>
            </div>
            <ul className="nav-list">
              {navMenu.map(item => (
                <li className="nav-list__item">
                  <a href="#" className="nav-list__link">{item}</a>
                  <img src={arrow_bottom} alt="arrow" className="nav-list__arrow" />
                </li>
              )
              )}
            </ul>
          </nav>
        </div>
      </header>
  );
}

export default Header;
