import React from "react";
import PostItem from "../PostItem/PostItem";
import './../../styles/main.css'
import './../PostList/index.css'
import Modal from "../Modal/Modal";


const PostList = ({posts, search}) => {

  const [selectedPost, setSelectedPost] = useState(null);

  const handlePostClick = (post) => {
    setSelectedPost(post);
  };

  const handleCloseModal = () => {
    setSelectedPost(null);
  };


  return ( 
    <div className="container">
      <div className="post-list">
        {posts.filter(obj => 
          (obj.title.toLowerCase().includes(search.toLocaleLowerCase()) || obj.text.toLowerCase().includes(search.toLocaleLowerCase()))
        ).map((post) =>
          <PostItem post={post} key={post.id} />
        )}
      </div>
      {selectedPost && (
        <Modal post={selectedPost} onCloseModal={handleCloseModal} />
      )}
    </div>
  );
}

export default PostList;
