import { useState } from 'react';
import './index.css'
import Modal from '../Modal/Modal';

const PostItem = (props) => {
  const [open, setOpen] = useState(false)

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };
  
  return ( 
    <>
      <div className={`post ${open ? "post--modal-open" : ""}`}  onClick={() => setOpen(true)}>
        <img className="post__img" src={props.post.img} alt="post"/>
        <div className="post__main">
          <div className="post__tag">{props.post.tags}</div>
          <h2 className="post__title">{props.post.title}</h2>
          <div className="post__info">
            <div className="post__autor">{props.post.autor}</div>
            <div className="post__date">{props.post.date}</div>
            <div className="post__views">{props.post.views}</div>
          </div>
          <p className="post__text">{props.post.text}</p>
        </div>
      </div>
      {open &&  <Modal setOpen={setOpen} title={props.post.title} text={props.post.text} onClose={handleClose} />}
    </>
  );
}

export default PostItem;
